import React from  'react';

import { connect } from 'react-redux';
import { Route } from 'react-router-dom';
import { firestore , convertCollectionsSnaoshotToMap } from '../../firebase/firebase.utils';

import CollectionPage from './collection/collection.component';
import CollectionsOverview from '../../components/collections-overview/collections-overview.component';
import { updateCollection} from '../../redux/shop/shop.actions';
class  ShopPage extends React.Component { 
    unsubscribeFromSnapshot = null; 

    componentDidMount(){
        const { updateCollection}  = this.props;
        const collectionRef = firestore.collection('collections');
        collectionRef.onSnapshot(async snapshot => {
         const collectionMap =  convertCollectionsSnaoshotToMap(snapshot)
         console.log(collectionMap);
         updateCollection(collectionMap)

        });

    }
    render() { 
        const { match } = this.props;

        return (    
    <div className='shop-page'>
      
        <Route exact path={`${match.path}`} component={CollectionsOverview} />
        <Route path ={ `${match.path}/:collectionId`} component={CollectionPage}/>
        
        
    </div>);
    }
}
const mapDispatchToProps = dispatch => ({
    updateCollection: collectionMap => dispatch(updateCollection(collectionMap))
})


export default connect(null,mapDispatchToProps)(ShopPage);
